-- Apagar agendamento anterior se existir
DELETE FROM agendamentos_volante WHERE DATE(data) = '2026-02-13';

-- Inserir novo agendamento com timestamp UTC correto (meia-noite UTC)
INSERT INTO agendamentos_volante (volanteId, lojaId, data, agendamento_volante_periodo, agendamento_volante_tipo, titulo, descricao)
VALUES (2, 180024, '2026-02-13 00:00:00', 'manha', 'outro', 'Teste Notificação', 'Agendamento de teste para verificar notificações Telegram');

-- Verificar
SELECT id, volanteId, lojaId, data, DATE(data) as data_apenas, agendamento_volante_periodo
FROM agendamentos_volante 
WHERE DATE(data) = '2026-02-13';

